import { NoteData } from "types/types";

export function renderEntityNote(params: {
  label: string;
  type: string;
  summary: string;
  interpretation: string;
  connections: string[];
  emotions: string[];
  notes: NoteData[];
}) {
  const {
    label,
    type,
    summary,
    interpretation,
    connections,
    emotions,
    notes
  } = params;

  const sourceLinks = notes.map(n => `- [[${n.file.path}]]`).join("\n");

  const emotionTags = (emotions || []).map(e =>
    `#emotion/${e.toLowerCase().replace(/\s+/g, "_")}`
  );
  const tags = [`#luma/${type}`, ...emotionTags, `#dream-symbol`].join(" ");

  return `**Entity Type:** ${type}  
**Recurring Emotions:** ${emotions?.join(", ") || "None"}  
**Appears In:**  
${sourceLinks}

---

## ğŸ” Overview

> ${summary}

---

## ğŸ”— Connections

${connections?.map(c => `- Related to [[${c}]]`).join("\n") || "- No connections detected."}

---

## ğŸ§  Interpretation

_autogenerated by Luma_  
${interpretation}

---

## ğŸ§¬ Tags

${tags}
`;
}
